import React, { useContext } from 'react';
import Profile from './pages/profile';
import Projects from './pages/projects';
import MessageToast from './pages/message-toast';
import { GlobalContext } from './context/GlobalState';
import Title from './components/title';
import TitleHover from './components/title-hover';

function App() {
  const { state } = useContext(GlobalContext);
  const { features } = state;
  return (
    <div className='app center-items'>
      <div className='container center-items'>
        <div className='hover-left'>
          <TitleHover text='Profile' side='left' />
        </div>
        {/* <div className='hover-right'>
          <TitleHover text='Projects' side='right' />
        </div> */}

        <Profile />
        <Projects />

        {features.openMessageToast ? <MessageToast /> : null}
      </div>
    </div>
  );
}

export default App;
