import React from 'react';
import Title from '../title/Title';

const TitleHover = ({ className = '', text = '', style = {}, display = false, side }) => (
  <div
    class={`title-hover ${className}  ${side === 'right' ? 'right' : 'left'} ${display ? 'opacity' : ''}`}
    style={style}
  >
    <Title className='title-hover-text' text={text} />
  </div>
);

export default TitleHover;
