import React from 'react';

const Input = ({ className = '', title = '', style = {}, action }) => (
  <div style={style} className={`input bold-text ${className}`}>
    <label for={title.toLowerCase()}>{title}</label>
    <input id={title.toLowerCase()} onChange={action} />
    <div className='underline'></div>
  </div>
);

export default Input;
