import React from 'react';
import Title from '../title';
import { socialIcons } from '../../constant/icons';

const Card = ({ title = '', jobTitle = '', location = '', links = [] }) => (
  <div className='card center-items'>
    <Title className='card-title' text={title} />
    <h3>{jobTitle}</h3>
    <h3>{location}</h3>

    <div className='cards-links center-items'>
      {links.length
        ? links.map((link) => {
            return (
              <a className='link' href={link.url}>
                {link.name}
                {socialIcons.github}
              </a>
            );
          })
        : null}
    </div>

    <button>Lets talk!</button>
  </div>
);

export default Card;
