import React, { useContext } from 'react';
import { GlobalContext } from '../../context/GlobalState';
import Card from '../../components/card';
import ProfilePic from '../../assets/images/profile.jpg';
import CardButton from '../../components/card-button';

const Profile = () => {
  const { state, toggleProfileCard } = useContext(GlobalContext);
  const { user } = state;

  const expendStyle = {
    height: '400px',
  };

  return (
    <div className='profile center-items' style={toggleProfileCard ? expendStyle : null}>
      <CardButton action={toggleProfileCard(true)} imgSrc={ProfilePic} />
      <Card title={user.name} jobTitle={user.jobTitle} location={user.location} links={user.socialLinks} />
    </div>
  );
};
export default Profile;
