import React, { useContext } from 'react';
import { GlobalContext } from '../../context/GlobalState';
import ProfilePic from '../../assets/images/ELD06141.jpg';
import Title from '../../components/title';

const Projects = () => {
  const { state, toggleProjectsCard } = useContext(GlobalContext);
  const { user, features } = state;
  const { openProjectsCard } = features;
  return (
    <div className={`projects center-items ${openProjectsCard ? 'expend' : ''} `}>
      <div
        onClick={() => toggleProjectsCard(!openProjectsCard)}
        className={`open-card-btn ${openProjectsCard ? 'open-card-btn-expend' : ''}`}
        style={{ backgroundImage: `url(${ProfilePic})` }}
      ></div>

      <div className={`card center-items ${openProjectsCard ? 'expend-card' : ''} `}>
        <div className={`card-titles center-items ${openProjectsCard ? 'expend-titles' : ''}`}>
          <Title className='card-title' text={user.name} />

          <h3 className='card-job-title'>
            {user.jobTitle} from <span>{user.location}</span>
          </h3>
        </div>

        <div className={`cards-links center-items ${openProjectsCard ? 'expend-card-links' : ''}`}>
          {user.socialLinks.length
            ? user.socialLinks.map((link) => (
                <a className='link center-items' href={link.url} style={link.style}>
                  {link.icon}
                </a>
              ))
            : null}
        </div>
      </div>
    </div>
  );
};

export default Projects;
