import React, { useContext } from 'react';
import { GlobalContext } from '../../context/GlobalState';
import Card from '../../components/card';
import ProfilePic from '../../assets/images/profile.jpg';
import CardButton from '../../components/card-button';

const Profile = () => {
  const { state, toggleProfileCard } = useContext(GlobalContext);
  const { user, features } = state;

  return (
    <div className={`profile center-items ${features.openProfileCard ? 'expend' : ''} `}>
      <div
        onClick={() => toggleProfileCard(!features.openProfileCard)}
        className='open-card-btn'
        style={{ backgroundImage: `url(${ProfilePic})` }}
      ></div>

      {/* <CardButton action={() => toggleProfileCard(!features.openProfileCard)} imgSrc={ProfilePic} /> */}
      <Card title={user.name} jobTitle={user.jobTitle} location={user.location} links={user.socialLinks} />
    </div>
  );
};
export default Profile;
