export default (state, action) => {
  switch (action.type) {
    case 'TOGGLE_PROFILE':
      return { ...state, features: { ...state.features, openProfileCard: action.payload } };

    case 'TOGGLE_PROJECTS':
      return { ...state, features: { ...state.features, openProjectsCard: action.payload } };

    case 'OPEN_MESSAGE_TOAST': {
      return { ...state, features: { ...state.features, openMessageToast: true } };
    }

    case 'CLOSE_MESSAGE_TOAST': {
      return { ...state, features: { ...state.features, openMessageToast: true } };
    }

    default:
      return state;
  }
};
